import React, { useState, useEffect } from 'react';
import { Wrench, Clock, CheckCircle, XCircle } from 'lucide-react';

const RepairHistory = () => {
  const defaultRepairs = [
    { id: 1, asset: 'Projector', room: 'Room 201', issue: 'Lamp failure', status: 'Completed', date: '2024-01-15', cost: '$150' },
    { id: 2, asset: 'Dell Laptop', room: 'Lab 3', issue: 'Screen damage', status: 'In Progress', date: '2024-01-18', cost: '$200' },
    { id: 3, asset: 'Air Conditioner', room: 'Office', issue: 'Not cooling', status: 'Pending', date: '2024-01-20', cost: '$300' }
  ];

  const [showModal, setShowModal] = useState(false);
  const [newRepair, setNewRepair] = useState({ assetName: '', location: '', issue: '' });

  const handleUpdateStatus = (id, newStatus) => {
    const updated = repairs.map(r => r.id === id ? {...r, status: newStatus} : r);
    setRepairs(updated);
    const savedRepairs = JSON.parse(localStorage.getItem('repairs') || '[]');
    const updatedRepairs = savedRepairs.map(r => r.id === id ? {...r, status: newStatus} : r);
    localStorage.setItem('repairs', JSON.stringify(updatedRepairs));
  };

  const handleSubmitRepair = () => {
    const repairReport = {
      id: Date.now(),
      assetName: newRepair.assetName,
      issue: newRepair.issue,
      location: newRepair.location,
      status: 'Pending',
      timestamp: new Date().toLocaleString()
    };
    const existingRepairs = JSON.parse(localStorage.getItem('repairs') || '[]');
    localStorage.setItem('repairs', JSON.stringify([...existingRepairs, repairReport]));

    const newRepairItem = {
      id: repairReport.id,
      asset: newRepair.assetName,
      room: newRepair.location,
      issue: newRepair.issue,
      status: 'Pending',
      date: repairReport.timestamp.split(',')[0],
      cost: 'TBD'
    };
    setRepairs([...repairs, newRepairItem]);
    setShowModal(false);
    setNewRepair({ assetName: '', location: '', issue: '' });
  };

  const [repairs, setRepairs] = useState(() => {
    const saved = localStorage.getItem('repairs');
    if (saved) {
      const savedRepairs = JSON.parse(saved);
      return [...defaultRepairs, ...savedRepairs.map(r => ({
        id: r.id,
        asset: r.assetName,
        room: r.location,
        issue: r.issue,
        status: r.status,
        date: r.timestamp.split(',')[0],
        cost: 'TBD'
      }))];
    }
    return defaultRepairs;
  });

  const statusConfig = {
    'Completed': { icon: CheckCircle, color: 'text-green-600', bg: 'bg-green-100', badge: 'badge-success' },
    'In Progress': { icon: Clock, color: 'text-blue-600', bg: 'bg-blue-100', badge: 'badge-info' },
    'Pending': { icon: XCircle, color: 'text-amber-600', bg: 'bg-amber-100', badge: 'badge-warning' }
  };

  return (
    <div className="p-6 space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold mb-2">Repair History</h1>
          <p className="text-gray-500">Track all repair requests and status</p>
        </div>
        <button onClick={() => setShowModal(true)} className="btn-primary flex items-center gap-2">
          <Wrench size={20} />
          Report New Repair
        </button>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="card">
          <div className="flex items-center gap-3">
            <div className="p-3 bg-green-100 text-green-600 rounded-lg"><CheckCircle size={24} /></div>
            <div><p className="text-sm text-gray-500">Completed</p><p className="text-2xl font-bold">1</p></div>
          </div>
        </div>
        <div className="card">
          <div className="flex items-center gap-3">
            <div className="p-3 bg-blue-100 text-blue-600 rounded-lg"><Clock size={24} /></div>
            <div><p className="text-sm text-gray-500">In Progress</p><p className="text-2xl font-bold">1</p></div>
          </div>
        </div>
        <div className="card">
          <div className="flex items-center gap-3">
            <div className="p-3 bg-amber-100 text-amber-600 rounded-lg"><XCircle size={24} /></div>
            <div><p className="text-sm text-gray-500">Pending</p><p className="text-2xl font-bold">1</p></div>
          </div>
        </div>
      </div>
      <div className="card">
        <div className="space-y-4">
          {repairs.map((repair) => {
            const config = statusConfig[repair.status];
            return (
              <div key={repair.id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                <div className="flex items-start justify-between">
                  <div className="flex items-start gap-4 flex-1">
                    <div className={`p-3 ${config.bg} rounded-lg ${config.color}`}><Wrench size={24} /></div>
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <h3 className="text-lg font-bold">{repair.asset}</h3>
                        <span className={config.badge}>{repair.status}</span>
                      </div>
                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm mb-3">
                        <div><p className="text-gray-500">Location</p><p className="font-medium">{repair.room}</p></div>
                        <div><p className="text-gray-500">Issue</p><p className="font-medium">{repair.issue}</p></div>
                        <div><p className="text-gray-500">Date</p><p className="font-medium">{repair.date}</p></div>
                        <div><p className="text-gray-500">Cost</p><p className="font-medium">{repair.cost}</p></div>
                      </div>
                      {repair.status === 'Pending' && (
                        <div className="flex gap-2">
                          <button onClick={() => handleUpdateStatus(repair.id, 'In Progress')} className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 text-sm font-medium">Start Repair</button>
                        </div>
                      )}
                      {repair.status === 'In Progress' && (
                        <div className="flex gap-2">
                          <button onClick={() => handleUpdateStatus(repair.id, 'Completed')} className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 text-sm font-medium">Mark Completed</button>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-2xl font-bold mb-4">Report New Repair</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Asset Name</label>
                <input type="text" placeholder="e.g., Projector" value={newRepair.assetName} onChange={(e) => setNewRepair({...newRepair, assetName: e.target.value})} className="w-full px-4 py-2 border rounded-lg" />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Location</label>
                <input type="text" placeholder="e.g., Room 201" value={newRepair.location} onChange={(e) => setNewRepair({...newRepair, location: e.target.value})} className="w-full px-4 py-2 border rounded-lg" />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Issue Description</label>
                <textarea placeholder="Describe the issue" value={newRepair.issue} onChange={(e) => setNewRepair({...newRepair, issue: e.target.value})} className="w-full px-4 py-2 border rounded-lg" rows="3"></textarea>
              </div>
            </div>
            <div className="flex gap-3 mt-6">
              <button onClick={handleSubmitRepair} className="flex-1 btn-primary">Submit</button>
              <button onClick={() => setShowModal(false)} className="flex-1 btn-secondary">Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RepairHistory;
